sys_script_client.script:
function onSubmit() {
   //Type appropriate comment here, and begin script below
   if (g_form.getValue('impact') == 1 && g_form.getValue('urgency') == 1 && !g_user.hasRoleExactly('x_1336879_collec_0.major_inc_mgr')){
		var acckn = confirm("The Priority of Incident is ready submit.{}\n\nPlease, select OK to submit or Cancel to return to the record.", g_user.firstName);
		if (!acckn) {
			g_form.addInfoMessage("Incident is not submitted");
			g_form.addInfoMessage("If base information is unavailable, use the Additional comments field to document.");

			g_form.showFieldMsg("category", 'Major Incident base field');
			g_form.showFieldMsg("cmdb_ci", "Major Incident base field");
			g_form.showFieldMsg("assignment_group", "Major Incident base field");
			g_form.showFieldMsg("short_desciption", "Major Incident base field");
		}

		return acckn;
	}  
}
