sys_script_include.script:
var AssignmentGroupUtils = Class.create();
AssignmentGroupUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	countGroupMembers: function() {
		var grpName = "";
		var message = "There are no member in this Assignment Group";
		var groupID = this.getParameter("sysparm_group_id");
		// GlideRecord() deprecated?
		var grpMems = new GlideRecordSecure("sys_user_grmember");
		grpMems.addQuery("group.sys_id", groupID);
		grpMems.query();

		if (grpMems.next()) {
			grpName = grpMems.getDisplayValue('group');
		}
		if (grpName != ""){
			message = "There are " + grpMems.getRowCount() + " members in the " + grpName + "group";		
		}
		return message;
	},
    type: 'AssignmentGroupUtils'
});
